sqlReaderQuery.value:
@concat(
  'SELECT TOP 1 schema_name, table_name FROM schema_table WHERE LOWER(table_name) = LOWER(''', 
  replace(item().name, '.csv', ''), 
  ''')'
)

value.value:
@concat(
  'SELECT TOP 1 ''['' + schema_name + ''].['' + table_name + '']'' AS full_table_name FROM schema_table WHERE LOWER(table_name) = LOWER(''', 
  replace(item().name, '.csv', ''), 
  ''')'
)
